import React from 'react';
import { motion } from 'framer-motion';
import './HeaderSlider.css';

const HeaderSlider = () => {
  return (
    <div className="header-slider">
      <motion.div
        className="slider-image"
        initial={{ x: 0, y: 0 }}
        animate={{ x: '100vw', y: '-100vh' }}
        transition={{ duration: 5, repeat: Infinity, repeatType: 'reverse', ease: 'linear' }}
      >
        <img src="https://media.tenor.com/3uZZ_aOLoNkAAAAi/flock-of-birds-birds.gif" alt="Flock of Birds" />
      </motion.div>
      <div className="text-container">
        <motion.h1
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1 }}
        >
          Welcome to Our Site
        </motion.h1>
        <motion.p
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          Discover amazing content here.
        </motion.p>
        <motion.button
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1, delay: 1 }}
        >
          Get Started
        </motion.button>
      </div>
    </div>
  );
};

export default HeaderSlider;
