import React from 'react';
import BIRDS from 'vanta/dist/vanta.birds.min';
import * as THREE from 'three';

class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.vantaRef = React.createRef();
    this.state = { vantaEffect: null };
  }

  componentDidMount() {
    // Ensure the vantaRef is properly initialized before applying the effect
    if (!this.state.vantaEffect && this.vantaRef.current) {
      this.setState({
        vantaEffect: BIRDS({
          el: this.vantaRef.current,
          THREE: THREE, // Pass the THREE object to Vanta.js
          // Add your customization options here
          backgroundColor: 0x000000, // Background color in hexadecimal
          color1: 0xff0000, // Color of the birds
          color2: 0x00ff00, // Another color for the birds
          birdSize: 1.5, // Size of the birds
          wingSpan: 20.0, // Wing span of the birds
          speedLimit: 4.0, // Speed limit for the birds
          separation: 50.0, // Separation between birds
          alignment: 50.0, // Alignment of the birds
          cohesion: 50.0 // Cohesion of the birds
        })
      });
    }
  }

  componentWillUnmount() {
    if (this.state.vantaEffect) this.state.vantaEffect.destroy();
  }

  render() {
    return (
      <div ref={this.vantaRef} style={{ width: '100%', height: '100vh' }}>
        Foreground content goes here
      </div>
    );
  }
}

export default MyComponent;
